import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./register.css";

export default function Register() {
  const [showPassword, setShowPassword] = useState(false);
  const [showPassword2, setShowPassword2] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    company: "",
    phone_number: "",
    role: "user"
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [submitError, setSubmitError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const navigate = useNavigate();
  const API_URL = "http://localhost:5000"; // Your backend URL

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Clear error for this field when user starts typing
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ""
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // Username validation
    if (!formData.username.trim()) {
      newErrors.username = "Le nom d'utilisateur est requis";
    } else if (formData.username.length < 3) {
      newErrors.username = "Le nom d'utilisateur doit contenir au moins 3 caract√®res";
    }

    // Email validation
    if (!formData.email) {
      newErrors.email = "L'email est requis";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = "Format d'email invalide";
    }

    // Password validation
    if (!formData.password) {
      newErrors.password = "Le mot de passe est requis";
    } else if (formData.password.length < 6) {
      newErrors.password = "Le mot de passe doit contenir au moins 6 caract√®res";
    }

     // Confirm password validation
    if (!formData.confirmPassword) {
      newErrors.confirmPassword = "Veuillez confirmer le mot de passe";
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas";
    }

    // Company validation
    if (!formData.company.trim()) {
      newErrors.company = "Le nom de l'entreprise est requis";
    }

    // Phone validation (basic)
    if (formData.phone_number && !/^[\d\s\+\-\(\)]{10,}$/.test(formData.phone_number.replace(/\s/g, ''))) {
      newErrors.phone_number = "Num√©ro de t√©l√©phone invalide";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitError("");
    setSuccessMessage("");

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const registerData = {
        username: formData.username,
        email: formData.email,
        password: formData.password,
        company: formData.company,
        phone_number: formData.phone_number || undefined,
        role: formData.role
      };

      // Call backend API for registration
      const response = await axios.post(`${API_URL}/api/AuthRoute/register`, registerData);

      if (response.data.success) {
        const { token, user } = response.data;

        // Store authentication data
        localStorage.setItem("token", token);
        localStorage.setItem("user", JSON.stringify(user));
        localStorage.setItem("isLoggedIn", "true");

        // Set default axios header
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

        setSuccessMessage("Compte cr√©√© avec succ√®s ! Redirection en cours...");

        // Redirect after 2 seconds
        setTimeout(() => {
          if (user.role === "admin") {
            navigate("/admin-dashboard");
          } else {
            navigate("/dashboard");
          }
        }, 2000);
      }
    } catch (err) {
      console.error("Register error:", err);

      if (err.response) {
        switch (err.response.status) {
          case 400:
            setSubmitError(err.response.data.message || "Donn√©es invalides");
            break;
          case 409:
            setSubmitError("Email ou nom d'utilisateur d√©j√† utilis√©");
            break;
          case 500:
            setSubmitError("Erreur serveur. Veuillez r√©essayer plus tard");
            break;
          default:
            setSubmitError("Erreur lors de l'inscription");
        }
      } else if (err.request) {
        setSubmitError("Impossible de se connecter au serveur");
      } else {
        setSubmitError("Une erreur est survenue");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-icon">
          <span role="img" aria-label="box">üì¶</span>
        </div>

        <h2 className="auth-title">Cr√©er un compte</h2>
        <p className="auth-subtitle">
          Rejoignez la plateforme de gestion de stock
        </p>

        <form className="auth-form" onSubmit={handleSubmit}>
          {/* Success Message */}
          {successMessage && (
            <div className="success-message">
              <span style={{ marginRight: "8px" }}>‚úÖ</span>
              {successMessage}
            </div>
          )}

          {/* Error Message */}
          {submitError && (
            <div className="error-message">
              <span style={{ marginRight: "8px" }}>‚ö†Ô∏è</span>
              {submitError}
            </div>
          )}

          {/* Username */}
          <label htmlFor="username">Nom d'utilisateur *</label>
          <div className="auth-input">
            <span className="icon">üë§</span>
            <input 
              id="username"
              name="username"
              type="text" 
              placeholder="johndoe"
              value={formData.username}
              onChange={handleChange}
              disabled={loading}
              className={errors.username ? "input-error" : ""}
            />
          </div>
          {errors.username && <span className="field-error">{errors.username}</span>}

          {/* Email */}
          <label htmlFor="email">Email *</label>
          <div className="auth-input">
            <span className="icon">‚úâÔ∏è</span>
            <input 
              id="email"
              name="email"
              type="email" 
              placeholder="votre@email.com"
              value={formData.email}
              onChange={handleChange}
              disabled={loading}
              className={errors.email ? "input-error" : ""}
            />
          </div>
          {errors.email && <span className="field-error">{errors.email}</span>}

          {/* Password */}
          <label htmlFor="password">Mot de passe *</label>
          <div className="auth-input">
            <span className="icon">üîí</span>
            <input
              id="password"
              name="password"
              type={showPassword ? "text" : "password"}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              value={formData.password}
              onChange={handleChange}
              disabled={loading}
              className={errors.password ? "input-error" : ""}
            />
            <span
              className="toggle-password"
              onClick={() => !loading && setShowPassword(!showPassword)}
              style={{ cursor: loading ? "not-allowed" : "pointer" }}
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>
          {errors.password && <span className="field-error">{errors.password}</span>}

          {/* Confirm Password */}
          <label htmlFor="confirmPassword">Confirmer le mot de passe *</label>
          <div className="auth-input">
            <span className="icon">üîí</span>
            <input
              id="confirmPassword"
              name="confirmPassword"
              type={showPassword2 ? "text" : "password"}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              value={formData.confirmPassword}
              onChange={handleChange}
              disabled={loading}
              className={errors.confirmPassword ? "input-error" : ""}
            />
            <span
              className="toggle-password"
              onClick={() => !loading && setShowPassword2(!showPassword2)}
              style={{ cursor: loading ? "not-allowed" : "pointer" }}
            >
              {showPassword2 ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>
          {errors.confirmPassword && <span className="field-error">{errors.confirmPassword}</span>}

          {/* Company */}
          <label htmlFor="company">Entreprise *</label>
          <div className="auth-input">
            <span className="icon">üè¢</span>
            <input 
              id="company"
              name="company"
              type="text" 
              placeholder="Nom de votre entreprise"
              value={formData.company}
              onChange={handleChange}
              disabled={loading}
              className={errors.company ? "input-error" : ""}
            />
          </div>
          {errors.company && <span className="field-error">{errors.company}</span>}

          {/* Phone */}
          <label htmlFor="phone_number">Num√©ro de t√©l√©phone</label>
          <div className="auth-input">
            <span className="icon">üìû</span>
            <input 
              id="phone_number"
              name="phone_number"
              type="text" 
              placeholder="+33 6 12 34 56 78"
              value={formData.phone_number}
              onChange={handleChange}
              disabled={loading}
              className={errors.phone_number ? "input-error" : ""}
            />
          </div>
          {errors.phone_number && <span className="field-error">{errors.phone_number}</span>}

          {/* Role */}
          <label htmlFor="role">R√¥le</label>
          <div className="auth-input">
            <span className="icon">üßë‚Äçüíº</span>
            <select
              id="role"
              name="role"
              value={formData.role}
              onChange={handleChange}
              disabled={loading}
            >
              <option value="user">Staff (Gestionnaire)</option>
              <option value="admin">Admin</option>
              <option value="manager">Manager</option>
            </select>
          </div>

          <button 
            type="submit" 
            className="auth-button"
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner" style={{ marginRight: "8px" }}>‚è≥</span>
                Cr√©ation en cours...
              </>
            ) : (
              "Cr√©er mon compte"
            )}
          </button>

          <p className="auth-switch">
            Vous avez d√©j√† un compte ? <a href="/login">Se connecter</a>
          </p>
        </form>
      </div>
    </div>
  );
}
