import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./register.css";

export default function Register() {
  const [showPassword, setShowPassword] = useState(false);
  const [showPassword2, setShowPassword2] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    company: "",
    phone_number: "",
    role: "user"
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [submitError, setSubmitError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const navigate = useNavigate();
  const API_URL = "http://localhost:5000"; // Your backend URL

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Clear error for this field when user starts typing
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ""
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // Username validation
    if (!formData.username.trim()) {
      newErrors.username = "Le nom d'utilisateur est requis";
    } else if (formData.username.length < 3) {
      newErrors.username = "Le nom d'utilisateur doit contenir au moins 3 caract√®res";
    }

    // Email validation
    if (!formData.email) {
      newErrors.email = "L'email est requis";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = "Format d'email invalide";
    }

    // Password validation
    if (!formData.password) {
      newErrors.password = "Le mot de passe est requis";
    } else if (formData.password.length < 6) {
      newErrors.password = "Le mot de passe doit contenir au moins 6 caract√®res";
    }

     // Confirm password validation
    if (!formData.confirmPassword) {
      newErrors.confirmPassword = "Veuillez confirmer le mot de passe";
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Les mots de passe ne correspondent pas";
    }

    // Company validation
    if (!formData.company.trim()) {
      newErrors.company = "Le nom de l'entreprise est requis";
    }

    // Phone validation (basic)
    if (formData.phone_number && !/^[\d\s\+\-\(\)]{10,}$/.test(formData.phone_number.replace(/\s/g, ''))) {
      newErrors.phone_number = "Num√©ro de t√©l√©phone invalide";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitError("");
    setSuccessMessage("");

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const registerData = {
        username: formData.username,
        email: formData.email,
        password: formData.password,
        company: formData.company,
        phone_number: formData.phone_number || undefined,
        role: formData.role
      };

      // Call backend API for registration
      const response = await axios.post(`${API_URL}/api/AuthRoute/register`, registerData);

      if (response.data.success) {
        const { token, user } = response.data;

        // Store authentication data
        localStorage.setItem("token", token);
        localStorage.setItem("user", JSON.stringify(user));
        localStorage.setItem("isLoggedIn", "true");

        // Set default axios header
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

        setSuccessMessage("Compte cr√©√© avec succ√®s ! Redirection en cours...");

        // Redirect after 2 seconds
        setTimeout(() => {
          if (user.role === "admin") {
            navigate("/admin-dashboard");
          } else {
            navigate("/dashboard");
          }
        }, 2000);
      }
    } catch (err) {
      console.error("Register error:", err);

      if (err.response) {
        switch (err.response.status) {
          case 400:
            setSubmitError(err.response.data.message || "Donn√©es invalides");
            break;
          case 409:
            setSubmitError("Email ou nom d'utilisateur d√©j√† utilis√©");
            break;
          case 500:
            setSubmitError("Erreur serveur. Veuillez r√©essayer plus tard");
            break;
          default:
            setSubmitError("Erreur lors de l'inscription");
        }
      } else if (err.request) {
        setSubmitError("Impossible de se connecter au serveur");
      } else {
        setSubmitError("Une erreur est survenue");
      }
    } finally {
      setLoading(false);
    }
