import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./landingpage.css";

export default function LandingPage() {
  const navigate = useNavigate();
  const [userName, setUserName] = useState('');
  const [userInitial, setUserInitial] = useState('');

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      setUserName(user.username || '');
      setUserInitial(user.username ? user.username.charAt(0).toUpperCase() : '');
    }
  }, []);

  const menuItems = [
    { icon: 'üìä', label: 'Accueil', id: 'dashboard' },
    { icon: 'üìä', label: 'Tableau de bord', id: 'tableau-de-bord' },
    { icon: 'üì¶', label: 'D√©p√¥ts', id: 'depots' },
    { icon: 'üìã', label: 'Mat√©riel', id: 'materiel' },
    { icon: 'üìà', label: 'Mouvements', id: 'movements' },
    { icon: 'üìÖ', label: 'R√©servations', id: 'reservations' },
    { icon: 'üë•', label: 'Gestion des utilisateurs', id: 'users' }
  ];

  const handleMenuClick = (id) => {
    if (id === 'dashboard') {
      navigate('/dashboard');
    } else if (id === 'tableau-de-bord') {
      navigate('/tableau-de-bord');
    } else if (id === 'depots') {
      navigate('/depots');
    } else if (id === 'materiel') {
      navigate('/materiel');
    } else {
      console.log(`Navigate to: ${id}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('isLoggedIn');
    navigate('/login');
  };

  return (
    <div className="landing-page-wrapper">
      {/* Sidebar */}
      {userName && (
        <aside className="landing-sidebar">
          <div className="sidebar-header">
            <div className="logo-box">
              <span className="logo-icon">üì¶</span>
            </div>
            <div className="logo-text">
              <h2>web</h2>
              <p>Gestion de stock</p>
            </div>
          </div>

          <nav className="sidebar-menu">
            {menuItems.map((item) => (
              <a
                key={item.id}
                href="#"
                className={`menu-item ${item.id === 'dashboard' ? 'active' : ''}`}
                onClick={(e) => {
                  e.preventDefault();
                  handleMenuClick(item.id);
                }}
              >
                <span className="menu-icon">{item.icon}</span>
                <span className="menu-label">{item.label}</span>
              </a>
            ))}
          </nav>
        </aside>
      )}

      <div className="landing-container">
        {/* Main Icon */}
        <div className="main-icon">
          üì¶
        </div>

        {/* Title */}
        <h1 className="landing-title">Plateforme de Gestion de Stock</h1>

        {/* Subtitle */}
        <p className="landing-subtitle">
          Solution compl√®te pour g√©rer votre mat√©riel √©v√©nementiel √† travers
          plusieurs entrep√¥ts et r√©gions.
        </p>

        {/* Features */}
        <div className="features-grid">
          <div className="feature-card">
            <div className="feature-icon blue">üè¨</div>
            <h2>Multi-Entrep√¥ts</h2>
            <p>G√©rez plusieurs d√©p√¥ts r√©partis dans diff√©rentes r√©gions</p>
          </div>

          <div className="feature-card">
            <div className="feature-icon purple">üìä</div>
            <h2>Tableaux de Bord</h2>
            <p>Visualisez vos stocks et mouvements en temps r√©el</p>
          </div>

          <div className="feature-card">
            <div className="feature-icon green">üßë‚Äçü§ù‚Äçüßë</div>
            <h2>Multi-Utilisateurs</h2>
            <p>Gestion des r√¥les et permissions pour votre √©quipe</p>
          </div>
        </div>

        {/* Buttons */}
        <div className="landing-buttons">
          {!userName ? (
            <>
              <Link to="/login" className="btn primary">Se connecter</Link>
              <Link to="/register" className="btn secondary">Cr√©er un compte</Link>
            </>
          ) : (
            <button onClick={handleLogout} className="btn primary">D√©connexion</button>
          )}
        </div>

        {/* User Profile */}
        {userName && (
          <div className="user-info">
            <span>{userName}</span>
            <div className="user-avatar">{userInitial}</div>
          </div>
        )}
      </div>
    </div>
  );
}
